package projetinho;

public class Main07 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.print("For: ");
		int sum = 0;
	    for(int i = 1; i <=15; i ++) {
	        sum += i;
	    }
	    System.out.println(sum);
		System.out.print("While: ");
	    int soma = 0;
	    int a = 1;
	    while (a <= 15) {
	      soma+=a;
	      a++;
	    }
	     System.out.print(soma);
	    }
	}



